import React, { Component } from 'react';
import { Parallax } from 'react-scroll-parallax';
import './Form.css';


class Form extends Component {

  constructor(props){
    super(props)
    this.state={
      name: '',
      email: '',
      message: '',
    }
  }

  handleChange(event){
    this.setState({
      name: event.target.value,
      email: event.target.value,
      message: event.target.value,

    })
  }

  render(){

    return(
      <form onSubmit={this.handleSubmit}>
      <p>
        <label>
          Your Name: <input type="text" name="name" value={this.state.name} onChange={this.handleChange} />
        </label>
      </p>
      <p>
        <label>
          Your Email: <input type="email" name="email" value={this.state.email} onChange={this.handleChange} />
        </label>
      </p>
      <p>
        <label>
          Message: <textarea name="message" value={this.state.message} onChange={this.handleChange} />
        </label>
      </p>
      <p>
        <button type="submit">Send</button>
      </p>
    </form>
    )
  }



